import { Component, Input, OnInit } from '@angular/core';
import { SharedService } from 'src/app/shared.service';
@Component({
  selector: 'app-add-edit-emp',
  templateUrl: './add-edit-emp.component.html',
  styleUrls: ['./add-edit-emp.component.css']
})
export class AddEditEmpComponent implements OnInit{
constructor() {}

@Input() emp:any;
  EmployeeID!: string;
  EmployeeName!:string;
  EmployeeDateOfBirth!:string;
  EmployeeFirstWorkDay!:string;
  EmployeeSalary!:string;
ngOnInit(): void{
  this.EmployeeID=this.emp.EmployeeID;
  this.EmployeeName=this.emp.EmployeeName;
  this.EmployeeDateOfBirth=this.emp.EmployeeDateOfBirth;
  this.EmployeeFirstWorkDay=this.emp.EmployeeFirstWorkDay;
  this.EmployeeSalary=this.emp.EmployeeSalary;
}

addEmployee(){
  var val = {EmployeeID:this.EmployeeID,
    EmployeeName:this.EmployeeName,
    EmployeeDateOfBirth:this.EmployeeDateOfBirth,
    EmployeeFirstWorkDay:this.EmployeeFirstWorkDay,
    EmployeeSalary:this.EmployeeSalary,}
}

}
